import React from "react";
import { Container, Form, Button, Row, Col } from "react-bootstrap";

const AddCharacterForm = (props) => {
  return (
    <div>
      <Container>
        <Form onSubmit={props.handleCharacterFormSubmit}>
          <Row>
            <Col>
              <Form.Control
                onChange={props.handleCharacterInputChange}
                value={props.characterFormState.name}
                type="text"
                name="name"
                placeholder="Character Name"
              />
            </Col>
            <Col>
              <Form.Control
                onChange={props.handleCharacterInputChange}
                value={props.characterFormState.player}
                type="text"
                name="player"
                placeholder="Player Name"
              />
            </Col>
          </Row>
          <Row>
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.weaponSkill}
              type="number"
              name="weaponSkill"
              placeholder="WS"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.ballisticSkill}
              type="number"
              name="ballisticSkill"
              placeholder="BS"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.strength}
              type="number"
              name="strength"
              placeholder="S"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.toughness}
              type="number"
              name="toughness"
              placeholder="T"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.agility}
              type="number"
              name="agility"
              placeholder="AG"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.intellegence}
              type="number"
              name="intellegence"
              placeholder="INT"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.willpower}
              type="number"
              name="willpower"
              placeholder="WP"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.fellowship}
              type="number"
              name="fellowship"
              placeholder="FEL"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.attacks}
              type="number"
              name="attacks"
              placeholder="ATK"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.wounds}
              type="number"
              name="wounds"
              placeholder="WND"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.strengthBonus}
              type="number"
              name="strengthBonus"
              placeholder="SB"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.toughnessBonus}
              type="number"
              name="toughnessBonus"
              placeholder="SB"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.movement}
              type="number"
              name="movement"
              placeholder="M"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.magic}
              type="number"
              name="magic"
              placeholder="MAG"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.insanityPoints}
              type="number"
              name="insanityPoints"
              placeholder="IP"
            />
            <Form.Control
              onChange={props.handleCharacterInputChange}
              value={props.characterFormState.fatePoints}
              type="number"
              name="fatePoints"
              placeholder="FP"
            />
          </Row>
          <Button variant="primary" type="submit" value="add character">
            Create Character
          </Button>
        </Form>
      </Container>
    </div>
  );
};

export default AddCharacterForm;
