import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Header from "./components/ui/Header";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Form, Button, Row, Col } from "react-bootstrap";
import SearchBar from "./components/ui/SearchBar";
import BestiaryGrid from "./components/beastiary/BestiaryGrid";
import Footer from "./components/ui/Footer";
import API from "./components/utils/API";
import Landing from "./components/landing/Landing";

function App() {
  // login state
  const [loginFormState, setloginFormState] = useState({
    email: "",
    password: "",
  });

  const [profileState, setProfileState] = useState({
    name: "",
    email: "",
    campaigns: [],
    token: "",
    id: "",
    isLoggedIn: false,
  });

  useEffect(fetchUserData, []);

  function fetchUserData() {
    const token = localStorage.getItem("token");
    API.getProfile(token).then((profileData) => {
      if (profileData) {
        setProfileState({
          name: profileData.name,
          email: profileData.email,
          campaigns: profileData.Campaigns,
          encounters: profileData.Encounters,
          token: token,
          id: profileData.id,
          isLoggedIn: true,
        });
      } else {
        localStorage.removeItem("token");
        setProfileState({
          name: "",
          email: "",
          tanks: [],
          token: "",
          id: "",
          isLoggedIn: false,
        });
      }
    });
  }

  const inputChange = (event) => {
    const { name, value } = event.target;
    setloginFormState({
      ...loginFormState,
      [name]: value,
    });
  };

  const formSubmit = (event) => {
    event.preventDefault();
    API.login(loginFormState).then((newToken) => {
      localStorage.setItem("token", newToken.token);
      API.getProfile(newToken.token).then((profileData) => {
        setProfileState({
          name: profileData.name,
          email: profileData.email,
          campaigns: profileData.Campaigns,
          id: profileData.id,
          isLoggedIn: true,
        });
      });
    });
  };

  // State for Beastiary Cards
  const [monsters, setMonsters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [query, setQuery] = useState("");

  useEffect(() => {
    const fetchItems = async () => {
      const res = await axios(
        `https://combattracker-api.herokuapp.com/api/monsters`
      );
      setMonsters(res.data);
      setIsLoading(false);
    };
    fetchItems();
  }, []);

  return (
    <div className="App">
      <Router>
        <Header />
        <div class="background">
          <Container className="container">
            <br />
            <Route exact path="/">

            {props.profile.isLoggedIn?null:<Row>
        <Col md={{ span: 4, offset: 4 }}>
          <Form onSubmit={props.formSubmit}>
            <Form.Group controlId="formBasicEmail">
              <Form.Control
                onChange={props.inputChange}
                value={props.loginFormState.email}
                type="text"
                name="email"
                placeholder="Enter email"
              />
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
              <Form.Control
                onChange={props.inputChange}
                value={props.loginFormState.password}
                type="password"
                name="password"
                placeholder="password"
              />
            </Form.Group>
            <Button variant="primary" type="submit" value="login">
              Submit
            </Button>
          </Form>
        </Col>
      </Row>}


              {/* <Landing
                profile={profileState}
                inputChange={inputChange}
                loginFormState={loginFormState}
                formSubmit={formSubmit}
              /> */}
            </Route>
            <Route exact path='/beastiary'>
              <SearchBar getQuery={(q) => setQuery(q)} />
              <div className="body-bg">
                <BestiaryGrid
                  isLoading={isLoading}
                  monsters={monsters}
                  query={query}
                />
              </div>
            </Route>
          </Container>
          <br />
          <Footer />
        </div>
      </Router>
    </div>
  );
}

export default App;
